import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Home = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative h-screen flex items-center justify-center bg-gradient-to-br from-coffee to-dark-brown">
        {/* Background overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-40"></div>

        {/* Hero content */}
        <motion.div 
          className="relative z-10 text-center text-white px-4"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <motion.h1 
            className="font-playfair text-5xl md:text-7xl font-bold mb-4"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3, duration: 0.8 }}
          >
            The Estate Deli
          </motion.h1>
          <motion.p 
            className="font-lato text-xl md:text-2xl mb-8 max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6, duration: 0.8 }}
          >
            Artisanal Coffee · Handcrafted Experience
          </motion.p>
          <motion.button 
            onClick={() => scrollToSection('about')}
            className="btn-primary text-lg px-8 py-4 hover:bg-light-coffee transition-all duration-300 transform hover:scale-105"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.9, duration: 0.8 }}
          >
            Explore Menu ↓
          </motion.button>
        </motion.div>

        {/* Decorative elements */}
        <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2 animate-bounce">
          <div className="w-6 h-10 border-2 border-white rounded-full flex justify-center">
            <div className="w-1 h-3 bg-white rounded-full mt-2"></div>
          </div>
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="section-padding bg-cream">
        <div className="max-w-4xl mx-auto text-center">
          <motion.h2 
            className="font-playfair text-4xl md:text-5xl font-bold text-dark-brown mb-8"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Our Story
          </motion.h2>
          <motion.p 
            className="font-lato text-lg md:text-xl text-coffee leading-relaxed mb-12"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.8 }}
            viewport={{ once: true }}
          >
            Every bite and every sip tells a story—from our family-run estate in Chikmagalur (since 1889), 
            to mom's desserts and soulful food... five generations of passion, gathered at your table.
          </motion.p>

          <div className="grid md:grid-cols-3 gap-8 mt-16">
            <motion.div 
              className="text-center"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1, duration: 0.8 }}
              viewport={{ once: true }}
            >
              <div className="text-4xl mb-4">🌱</div>
              <h3 className="font-playfair text-xl font-semibold text-dark-brown mb-2">Estate Grown</h3>
              <p className="text-coffee">Coffee beans from our family estate in Chikmagalur since 1889</p>
            </motion.div>

            <motion.div 
              className="text-center"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.3, duration: 0.8 }}
              viewport={{ once: true }}
            >
              <div className="text-4xl mb-4">👨‍🍳</div>
              <h3 className="font-playfair text-xl font-semibold text-dark-brown mb-2">Handcrafted</h3>
              <p className="text-coffee">Every cup and dish prepared with artisanal care and tradition</p>
            </motion.div>

            <motion.div 
              className="text-center"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5, duration: 0.8 }}
              viewport={{ once: true }}
            >
              <div className="text-4xl mb-4">❤️</div>
              <h3 className="font-playfair text-xl font-semibold text-dark-brown mb-2">Family Legacy</h3>
              <p className="text-coffee">Five generations of passion and dedication to quality</p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Quick Menu Preview */}
      <section className="section-padding bg-white">
        <div className="max-w-6xl mx-auto">
          <motion.h2 
            className="font-playfair text-4xl md:text-5xl font-bold text-dark-brown text-center mb-12"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Featured Menu
          </motion.h2>

